    private void btn_addBookActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_addBookActionPerformed
        addBook();
        PDFDisplay.setText("Successfully added!");
        txt_num.setText("");
        txt_name.setText("");
        txt_genre.setText("");
    }//GEN-LAST:event_btn_addBookActionPerformed

    private void btn_TakeOutBookActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_TakeOutBookActionPerformed
        clearText();
        Book book = lib.getBook(Integer.parseInt(txt_choice.getText()) - 1);
        
        try
        {
            PDFDisplay.setText(book.readBook());
        }
        catch (Exception e)
        {
            PDFDisplay.setText("Not valid");
        }     
        btn_TakeOutBook.setText("");
    }//GEN-LAST:event_btn_TakeOutBookActionPerformed

    private void btn_checkBooksActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_checkBooksActionPerformed
        clearText();
        PDFDisplay.setText(lib.checkInventory());
    }//GEN-LAST:event_btn_checkBooksActionPerformed

