    private void btn_DefineActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btn_DefineActionPerformed
        try
        {    
            String defintion = note.gptRequest(txt_Define.getText());
            note.writeToNote(defintion);
            txt_Define.setText("");
        }
        catch (Exception e)
        {
            PDFDisplay.setText("An error has ocurred: " + e.getMessage());
        }
                
        JOptionPane.showMessageDialog(null, "Definition inserted into text document");
    }//GEN-LAST:event_btn_DefineActionPerformed

