    Library lib = new Library();
    NoteBook note = new NoteBook();
    
    public void addBook()
    {
        Book newBook = new Book(Integer.parseInt(txt_num.getText()), txt_name.getText(), txt_genre.getText());
        lib.add(newBook);
    }
    
    public void clearText()
    {
        PDFDisplay.setText((""));
    }
    
